server {
    listen 80;
    server_name 145.241.111.141;

    client_max_body_size 26M;

    # Serve Next.js static assets directly
    location /_next/static/ {
        alias /home/opc/TECHVISIONS-EMPLOYEE-DOCUMENTS/.next/static/;
        expires 365d;
        access_log off;
        add_header Cache-Control "public, immutable";
    }

    # Serve public folder assets directly
    location /public/ {
        alias /home/opc/TECHVISIONS-EMPLOYEE-DOCUMENTS/public/;
        expires 30d;
        access_log off;
    }

    # Serve files from public folder at root level \(e.g. /logo.svg\)
    location ~* ^/\(.+\\.\(svg|png|jpg|jpeg|gif|ico|webp|avif|mp4|webm\)\)$ {
        root /home/opc/TECHVISIONS-EMPLOYEE-DOCUMENTS/public;
        try_files /$1 @backend;
        expires 30d;
        access_log off;
    }

    # Fallback to Next.js app
    location @backend {
        proxy_pass http://127.0.0.1:3000;
        proxy_http_version 1.1;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection "upgrade";
    }

    location / {
        proxy_pass http://127.0.0.1:3000;
        proxy_http_version 1.1;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection "upgrade";
    }
}

